import React, { useState } from "react";
import DropzoneUpload from "./DropzoneUpload";

function CompressPdf() {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleCompress = async () => {
    if (!file) return alert("üìÑ Please select a PDF to compress.");

    const formData = new FormData();
    formData.append("file", file);
    formData.append("type", "compress-pdf");

    setLoading(true);
    try {
      const res = await fetch("http://localhost:5001/convert", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) throw new Error(await res.text());

      const blob = await res.blob();
      const url = URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = url;
      link.download = file.name.replace(/\.pdf$/, "_compressed.pdf");
      link.click();
      URL.revokeObjectURL(url);
    } catch (err) {
      console.error("‚ùå Compression Error:", err);
      alert("Compression failed: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      padding: "1rem",
      border: "1px solid #ccc",
      borderRadius: "8px",
      maxWidth: "400px",
      margin: "1rem auto",
      background: "#f9f9f9",
      textAlign: "center"
    }}>
      <h3>üóú Compress PDF</h3>

      <DropzoneUpload
        onFilesSelected={setFile}
        accept="application/pdf"
        multiple={false}
      />

      {file && <p>‚úÖ Selected: {file.name}</p>}

      <button
        onClick={handleCompress}
        disabled={loading}
        style={{
          marginTop: "10px",
          padding: "8px 16px",
          backgroundColor: "#007bff",
          color: "#fff",
          border: "none",
          borderRadius: "4px",
          cursor: loading ? "not-allowed" : "pointer",
        }}
      >
        {loading ? "üóú Compressing..." : "üóú Compress PDF"}
      </button>
    </div>
  );
}

export default CompressPdf;
