import React, { useState } from "react";
import DropzoneUpload from "./DropzoneUpload";

function OcrPdf() {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleOCR = async () => {
    if (!file) return alert("üìÑ Please select a scanned PDF");

    const formData = new FormData();
    formData.append("file", file);
    formData.append("type", "ocr-pdf");

    setLoading(true);
    try {
      const res = await fetch("http://localhost:5001/convert", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) {
        const errText = await res.text();
        throw new Error(errText || "OCR failed");
      }

      const blob = await res.blob();
      const url = URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = url;
      link.download = file.name.replace(/\.pdf$/, ".txt");
      link.click();
      URL.revokeObjectURL(url);
    } catch (err) {
      console.error("‚ùå OCR Error:", err);
      alert("OCR failed: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h3>üîç OCR PDF</h3>

      {/* ‚úÖ Drag & Drop File Upload */}
      <DropzoneUpload
        onFilesSelected={(f) => setFile(f)}
        accept="application/pdf"
      />

      {file && <p>‚úÖ Selected: {file.name}</p>}

      <button onClick={handleOCR} disabled={loading}>
        {loading ? "üîç Extracting..." : "üîç OCR PDF"}
      </button>
    </div>
  );
}

export default OcrPdf;
