// File: src/components/DropzoneUpload.jsx
import React, { useCallback } from "react";
import { useDropzone } from "react-dropzone";

function DropzoneUpload({ onFilesSelected, accept, multiple = false }) {
  const onDrop = useCallback(
    (acceptedFiles) => {
      if (acceptedFiles.length > 0) {
        if (multiple) {
          onFilesSelected(acceptedFiles); // send all files
        } else {
          onFilesSelected(acceptedFiles[0]); // send only first
        }
      }
    },
    [onFilesSelected, multiple]
  );

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept,
    multiple,
  });

  return (
    <div
      {...getRootProps()}
      style={{
        border: "2px dashed #aaa",
        borderRadius: "8px",
        padding: "20px",
        textAlign: "center",
        backgroundColor: isDragActive ? "#eef" : "#fafafa",
        cursor: "pointer",
        marginBottom: "10px",
      }}
    >
      <input {...getInputProps()} />
      {isDragActive
        ? "ðŸ“‚ Drop the file(s) here..."
        : `ðŸ“‚ Drag & Drop your file${multiple ? "s" : ""} here or click to browse`}
    </div>
  );
}

export default DropzoneUpload;
