import React, { useState } from "react";

function MergePdf() {
  const [files, setFiles] = useState([]);
  const [merging, setMerging] = useState(false);

  const handleUpload = async () => {
    if (files.length < 2) {
      alert("Please select at least 2 PDF files to merge.");
      return;
    }

    try {
      setMerging(true);
      const formData = new FormData();
      for (let file of files) {
        formData.append("file", file);  // merge expects a single zip file, so this may need adjusting
      }
      formData.append("type", "merge-pdf"); // âœ… Add action type

      const res = await fetch("http://localhost:5001/convert", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) throw new Error("Merge failed");

      const blob = await res.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = url;
      link.download = "merged.pdf";
      link.click();
    } catch (err) {
      alert("âŒ Merging failed. Please check the files or try again.");
      console.error(err);
    } finally {
      setMerging(false);
    }
  };

  return (
    <div style={{ marginTop: "10px" }}>
      <h2>ðŸ“Ž Merge PDF Files</h2>
      <input
        type="file"
        accept="application/pdf"
        multiple
        onChange={(e) => {
          setFiles([...e.target.files]);
        }}
      />
      <button onClick={handleUpload} disabled={merging}>
        {merging ? "ðŸ”„ Merging..." : "ðŸ”— Merge PDFs"}
      </button>
    </div>
  );
}

export default MergePdf;
