import React, { useState } from "react";
import DropzoneUpload from "./DropzoneUpload";

function DocxToPdf() {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [blob, setBlob] = useState(null);

  const handleUpload = async () => {
    if (!file) return alert("‚ùó Please select a DOCX file");

    const formData = new FormData();
    formData.append("file", file);
    formData.append("type", "docx-to-pdf");

    setLoading(true);
    try {
      const res = await fetch("http://localhost:5001/convert", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) throw new Error(await res.text());

      const blobData = await res.blob();
      setBlob(blobData);
    } catch (err) {
      console.error("‚ùå Conversion failed:", err);
      alert("Conversion failed: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = () => {
    if (!blob) return;
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = file.name.replace(/\.docx$/, ".pdf");
    link.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div>
      <h3>üìÑ DOCX ‚û°Ô∏è PDF</h3>

      {/* ‚úÖ Drag & Drop Upload */}
      <DropzoneUpload onFilesSelected={(f) => {
        setFile(f);
        setBlob(null); // Clear any previous result
      }} accept=".docx" />

      {file && <p>‚úÖ Selected: {file.name}</p>}

      <button onClick={handleUpload} disabled={loading}>
        {loading ? "‚è≥ Converting..." : "üîÅ Convert"}
      </button>

      {blob && (
        <button onClick={handleDownload} style={{ marginLeft: "10px" }}>
          ‚¨áÔ∏è Download
        </button>
      )}
    </div>
  );
}

export default DocxToPdf;
