import React, { useRef } from "react";
import "./DropzoneUpload.css"; // Optional styling

function DropzoneUpload({ onFilesSelected, accept = "application/pdf", multiple = false }) {
  const inputRef = useRef();

  const handleDrop = (e) => {
    e.preventDefault();
    const files = Array.from(e.dataTransfer.files).filter(file =>
      file.type === accept || accept === "*/*"
    );
    if (files.length) onFilesSelected(multiple ? files : files[0]);
  };

  const handleBrowse = (e) => {
    const files = Array.from(e.target.files).filter(file =>
      file.type === accept || accept === "*/*"
    );
    if (files.length) onFilesSelected(multiple ? files : files[0]);
  };

  return (
    <div
      className="dropzone"
      onDragOver={(e) => e.preventDefault()}
      onDrop={handleDrop}
      onClick={() => inputRef.current.click()}
    >
      <p>ðŸ“‚ Drag & Drop your file here or click to browse</p>
      <input
        type="file"
        ref={inputRef}
        style={{ display: "none" }}
        accept={accept}
        multiple={multiple}
        onChange={handleBrowse}
      />
    </div>
  );
}

export default DropzoneUpload;
