import React, { useState } from "react";
import { PDFDocument, degrees } from "pdf-lib";
import { Document, Page, pdfjs } from "react-pdf";

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

function ReorderPdf() {
  const [file, setFile] = useState(null);
  const [pages, setPages] = useState([]);
  const [rotations, setRotations] = useState({});
  const [blob, setBlob] = useState(null);
  const [numPages, setNumPages] = useState(null);

  const handleFile = (f) => {
    setFile(f);
    setBlob(null);
    setPages([]);
    setRotations({});
    setNumPages(null);
  };

  const onDocLoad = ({ numPages }) => {
    setNumPages(numPages);
    setPages(Array.from({ length: numPages }, (_, i) => i));
  };

  const movePage = (index, direction) => {
    const newPages = [...pages];
    const targetIndex = index + direction;

    if (targetIndex >= 0 && targetIndex < newPages.length) {
      const temp = newPages[index];
      newPages[index] = newPages[targetIndex];
      newPages[targetIndex] = temp;
      setPages(newPages);
    }
  };

  const moveToStart = (index) => {
    const newPages = [...pages];
    const [moved] = newPages.splice(index, 1);
    newPages.unshift(moved);
    setPages(newPages);
  };

  const moveToEnd = (index) => {
    const newPages = [...pages];
    const [moved] = newPages.splice(index, 1);
    newPages.push(moved);
    setPages(newPages);
  };

  const rotateLeft = (index) => {
    setRotations((prev) => ({
      ...prev,
      [index]: ((prev[index] || 0) - 90) % 360,
    }));
  };

  const rotateRight = (index) => {
    setRotations((prev) => ({
      ...prev,
      [index]: ((prev[index] || 0) + 90) % 360,
    }));
  };

  const deletePage = (index) => {
    const newPages = [...pages];
    newPages.splice(index, 1);
    setPages(newPages);

    const newRotations = { ...rotations };
    delete newRotations[pages[index]];
    setRotations(newRotations);
  };

  const handleReorder = async () => {
    const origPdf = await PDFDocument.load(await file.arrayBuffer());
    const newPdf = await PDFDocument.create();

    for (const i of pages) {
      const [copied] = await newPdf.copyPages(origPdf, [i]);
      const page = copied;
      const rotateAngle = (rotations[i] || 0) % 360;
      if (rotateAngle !== 0) {
        page.setRotation(degrees(rotateAngle));
      }
      newPdf.addPage(page);
    }

    const pdfBytes = await newPdf.save();
    const newBlob = new Blob([pdfBytes], { type: "application/pdf" });
    setBlob(newBlob);
  };

  return (
    <div className="tool-box">
      <h3>üìÑ Reorder PDF Pages</h3>

      <input
        type="file"
        accept="application/pdf"
        onChange={(e) => handleFile(e.target.files[0])}
      />

      {file && (
        <Document file={file} onLoadSuccess={onDocLoad}>
          <div className="reorder-preview">
            {pages.map((pageIndex, idx) => (
              <div key={idx} className="page-thumbnail">
                <Page
                  pageNumber={pageIndex + 1}
                  width={120}
                  rotate={(rotations[pageIndex] || 0) % 360}
                  renderAnnotationLayer={false}
                  renderTextLayer={false}
                />
                <p style={{ fontSize: "12px", textAlign: "center", marginTop: "4px" }}>
                  Page {pageIndex + 1}
                </p>

                {/* Movement Controls */}
                <div style={{ display: "flex", justifyContent: "center", gap: "4px", flexWrap: "wrap" }}>
                  <button disabled={idx === 0} onClick={() => movePage(idx, -1)}>üîº</button>
                  <button disabled={idx === pages.length - 1} onClick={() => movePage(idx, 1)}>üîΩ</button>
                  <button onClick={() => moveToStart(idx)}>‚èÆÔ∏è</button>
                  <button onClick={() => moveToEnd(idx)}>‚è≠Ô∏è</button>
                </div>

                {/* Rotation Controls */}
                <div style={{ display: "flex", justifyContent: "center", gap: "4px", marginTop: "4px" }}>
                  <button onClick={() => rotateLeft(pageIndex)}>‚§∫</button>
                  <button onClick={() => rotateRight(pageIndex)}>‚§ª</button>
                </div>

                {/* Delete Page */}
                <div style={{ display: "flex", justifyContent: "center", marginTop: "4px" }}>
                  <button onClick={() => deletePage(idx)} style={{ background: "#e74c3c", color: "#fff" }}>
                    ‚ùå Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        </Document>
      )}

      {pages.length > 0 && (
        <div style={{ marginTop: "10px", display: "flex", gap: "10px" }}>
          <button onClick={handleReorder}>üìÑ Regenerate</button>
          <button onClick={() => handleFile(null)}>üîÅ Reset</button>
        </div>
      )}

      {blob && (
        <button
          style={{ marginTop: "10px" }}
          onClick={() => {
            const url = URL.createObjectURL(blob);
            const link = document.createElement("a");
            link.href = url;
            link.download = "updated.pdf";
            link.click();
          }}
        >
          ‚¨áÔ∏è Download
        </button>
      )}
    </div>
  );
}

export default ReorderPdf;
