// src/components/DocxToPdf.jsx
import React, { useState } from "react";

function DocxToPdf() {
  const [file, setFile] = useState(null);

  const handleUpload = async () => {
    if (!file) return alert("Please select a DOCX file");

    const formData = new FormData();
    formData.append("docx_file", file);

    const res = await fetch("http://localhost:5001/convert-docx-to-pdf", {
      method: "POST",
      body: formData,
    });

    const blob = await res.blob();
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = "converted.pdf";
    link.click();
  };

  return (
    <div>
      <h2>ðŸ“„ DOCX to PDF</h2>
      <input type="file" accept=".docx" onChange={(e) => setFile(e.target.files[0])} />
      <button onClick={handleUpload}>Convert</button>
    </div>
  );
}

export default DocxToPdf;
