import React, { useState } from "react";
import { PDFDocument } from "pdf-lib";
import { Document, Page } from "react-pdf";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

function ReorderPdf() {
  const [file, setFile] = useState(null);
  const [pages, setPages] = useState([]);
  const [blob, setBlob] = useState(null);
  const [numPages, setNumPages] = useState(null);

  const handleFile = (f) => {
    setFile(f);
    setBlob(null);
    setPages([]);
    setNumPages(null);
  };

  const onDocLoad = ({ numPages }) => {
    setNumPages(numPages);
    setPages(Array.from({ length: numPages }, (_, i) => i));
  };

  const handleReorder = async () => {
    const origPdf = await PDFDocument.load(await file.arrayBuffer());
    const newPdf = await PDFDocument.create();

    for (const i of pages) {
      const [copied] = await newPdf.copyPages(origPdf, [i]);
      newPdf.addPage(copied);
    }

    const pdfBytes = await newPdf.save();
    const newBlob = new Blob([pdfBytes], { type: "application/pdf" });
    setBlob(newBlob);
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;
    const reordered = Array.from(pages);
    const [moved] = reordered.splice(result.source.index, 1);
    reordered.splice(result.destination.index, 0, moved);
    setPages(reordered);
  };

  return (
    <div className="tool-box">
      <h3>üìÑ Reorder PDF Pages</h3>

      <label className="btn" style={{ marginBottom: "10px", cursor: "pointer" }}>
        üìÇ Select PDF
        <input type="file" accept="application/pdf" onChange={(e) => handleFile(e.target.files[0])} hidden />
      </label>

      {file && (
        <>
          <Document file={file} onLoadSuccess={onDocLoad} />

          {numPages && pages.length > 0 && (
            <DragDropContext onDragEnd={handleDragEnd}>
              <Droppable droppableId="pages" direction="horizontal">
                {(provided) => (
                  <div
                    className="reorder-preview"
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                  >
                    {pages.map((pageIndex, idx) => (
                      <Draggable key={pageIndex} draggableId={`${pageIndex}`} index={idx}>
                        {(provided) => (
                          <div
                            className="page-thumbnail"
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                          >
                            <Page file={file} pageNumber={pageIndex + 1} width={100} />
                          </div>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            </DragDropContext>
          )}

          <div style={{ display: "flex", gap: "10px", marginTop: "12px", flexWrap: "wrap" }}>
            <button className="btn bg-green-600" onClick={handleReorder}>
              üîÄ Reorder & Generate
            </button>

            {blob && (
              <button
                className="btn"
                onClick={() => {
                  const url = URL.createObjectURL(blob);
                  const link = document.createElement("a");
                  link.href = url;
                  link.download = "reordered.pdf";
                  link.click();
                }}
              >
                ‚¨áÔ∏è Download
              </button>
            )}

            <button className="btn bg-red-600" onClick={() => setFile(null)}>
              ‚ùå Remove File
            </button>
          </div>
        </>
      )}
    </div>
  );
}

export default ReorderPdf;
