import React, { useState, useRef } from "react";
import { Document, Page, pdfjs } from "react-pdf";
import { PDFDocument, rgb, StandardFonts } from "pdf-lib";
import "./App.css";

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

function App() {
  const [pdfFile, setPdfFile] = useState(null);
  const [numPages, setNumPages] = useState(null);
  const [textElements, setTextElements] = useState([]);
  const [selectedTextId, setSelectedTextId] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [fontSize, setFontSize] = useState(12);
  const [fontColor, setFontColor] = useState("#000000");
  const fileInputRef = useRef();

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type === "application/pdf") {
      setPdfFile(file);
      setTextElements([]);
    }
  };

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
  };

  const handlePageClick = (e, pageIndex) => {
    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const input = prompt("Enter text to insert:");
    if (input) {
      setTextElements((prev) => [
        ...prev,
        {
          id: Date.now(),
          page: pageIndex,
          x,
          y,
          text: input,
          size: fontSize,
          color: fontColor,
          bold: false,
          italic: false,
          underline: false,
        },
      ]);
    }
  };

  const handleDownload = async () => {
    if (!pdfFile) return;
    const arrayBuffer = await pdfFile.arrayBuffer();
    const pdfDoc = await PDFDocument.load(arrayBuffer);
    const helveticaFont = await pdfDoc.embedFont(StandardFonts.Helvetica);

    textElements.forEach((el) => {
      const page = pdfDoc.getPage(el.page);
      const { width, height } = page.getSize();
      page.drawText(el.text, {
        x: el.x,
        y: height - el.y - el.size,
        size: el.size,
        font: helveticaFont,
        color: rgb(
          parseInt(el.color.slice(1, 3), 16) / 255,
          parseInt(el.color.slice(3, 5), 16) / 255,
          parseInt(el.color.slice(5, 7), 16) / 255
        ),
      });
    });

    const modifiedPdf = await pdfDoc.save();
    const blob = new Blob([modifiedPdf], { type: "application/pdf" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "EditedPDF.pdf";
    link.click();
  };

  const startDrag = (e, id) => {
    e.stopPropagation();
    const el = textElements.find((t) => t.id === id);
    setSelectedTextId(id);
    setDragOffset({ x: e.clientX - el.x, y: e.clientY - el.y });
  };

  const onDrag = (e) => {
    if (!selectedTextId) return;
    setTextElements((prev) =>
      prev.map((t) =>
        t.id === selectedTextId
          ? { ...t, x: e.clientX - dragOffset.x, y: e.clientY - dragOffset.y }
          : t
      )
    );
  };

  const stopDrag = () => {
    
  };

  const deleteText = (id) => {
    setTextElements((prev) => prev.filter((t) => t.id !== id));
  };

  return (
    <div
        className="container"
        onMouseMove={onDrag}
        onMouseUp={stopDrag}
        onClick={() => setSelectedTextId(null)} // ‚úÖ click outside to dismiss
        >

      <h1>üìù SafeEditPDF</h1>

      <div className="controls">
        <input
          ref={fileInputRef}
          type="file"
          accept="application/pdf"
          onChange={handleFileChange}
          style={{ display: "none" }}
        />
        <button onClick={() => fileInputRef.current.click()} className="btn">
          üìÇ Upload PDF
        </button>

        <input
          type="number"
          value={fontSize}
          onChange={(e) => setFontSize(parseInt(e.target.value))}
          min="8"
          max="48"
          className="input"
        />

        <input
          type="color"
          value={fontColor}
          onChange={(e) => setFontColor(e.target.value)}
          className="input"
        />

        <button onClick={handleDownload} className="btn bg-green-600">
          üíæ Download PDF
        </button>
      </div>

      {pdfFile && (
        <Document file={pdfFile} onLoadSuccess={onDocumentLoadSuccess} className="pdf-container">
          {Array.from(new Array(numPages), (_, index) => (
            <div key={index} style={{ position: "relative", marginBottom: "1rem" }}>
              <Page
                pageNumber={index + 1}
                width={600}
                onClick={(e) => handlePageClick(e, index)}
              />
              <div
                className="overlay"
                style={{ position: "absolute", top: 0, left: 0, width: 600, height: "100%" }}
              >
                {textElements
                  .filter((t) => t.page === index)
                  .map((t) => (
                    <div
                      key={t.id}
                      style={{
                        position: "absolute",
                        left: t.x,
                        top: t.y,
                        fontSize: t.size,
                        color: t.color,
                        fontWeight: t.bold ? "bold" : "normal",
                        fontStyle: t.italic ? "italic" : "normal",
                        textDecoration: t.underline ? "underline" : "none",
                        cursor: "move",
                        background: "#fff",
                        padding: "2px",
                        border: "1px dashed #ccc",
                      }}
                      onMouseDown={(e) => startDrag(e, t.id)}
                      onDoubleClick={() => deleteText(t.id)}
                      title="Double click to delete"
                    >
                      {selectedTextId === t.id && (
                        <div className="text-toolbar">
                          <button
                            className="toolbar-btn"
                            onClick={(e) => {
                              e.stopPropagation();
                              setTextElements((prev) =>
                                prev.map((el) =>
                                  el.id === t.id ? { ...el, bold: !el.bold } : el
                                )
                              );
                            }}
                          >
                            <b>B</b>
                          </button>
                          <button
                            className="toolbar-btn"
                            onClick={(e) => {
                              e.stopPropagation();
                              setTextElements((prev) =>
                                prev.map((el) =>
                                  el.id === t.id ? { ...el, italic: !el.italic } : el
                                )
                              );
                            }}
                          >
                            <i>I</i>
                          </button>
                          <button
                            className="toolbar-btn"
                            onClick={(e) => {
                              e.stopPropagation();
                              setTextElements((prev) =>
                                prev.map((el) =>
                                  el.id === t.id ? { ...el, underline: !el.underline } : el
                                )
                              );
                            }}
                          >
                            <u>U</u>
                          </button>
                          <input
                            type="number"
                            min="8"
                            max="48"
                            value={t.size}
                            onChange={(e) => {
                              const newSize = parseInt(e.target.value);
                              setTextElements((prev) =>
                                prev.map((el) =>
                                  el.id === t.id ? { ...el, size: newSize } : el
                                )
                              );
                            }}
                            style={{ width: "40px" }}
                          />
                          <input
                            type="color"
                            value={t.color}
                            onChange={(e) => {
                              const newColor = e.target.value;
                              setTextElements((prev) =>
                                prev.map((el) =>
                                  el.id === t.id ? { ...el, color: newColor } : el
                                )
                              );
                            }}
                          />
                        </div>
                      )}
                      {t.text}
                    </div>
                  ))}
              </div>
            </div>
          ))}
        </Document>
      )}
    </div>
  );
}

export default App;
