import React, { useState } from "react";
import { PDFDocument } from "pdf-lib";
import { Document, Page, pdfjs } from "react-pdf";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

function ReorderPdf() {
  const [file, setFile] = useState(null);
  const [pages, setPages] = useState([]);
  const [blob, setBlob] = useState(null);
  const [numPages, setNumPages] = useState(null);

  const handleFile = (f) => {
    setFile(f);
    setBlob(null);
    setPages([]);
    setNumPages(null);
  };

  const onDocLoad = ({ numPages }) => {
    setNumPages(numPages);
    setPages(Array.from({ length: numPages }, (_, i) => i));
  };

  const handleReorder = async () => {
    const origPdf = await PDFDocument.load(await file.arrayBuffer());
    const newPdf = await PDFDocument.create();

    for (const i of pages) {
      const [copied] = await newPdf.copyPages(origPdf, [i]);
      newPdf.addPage(copied);
    }

    const pdfBytes = await newPdf.save();
    const newBlob = new Blob([pdfBytes], { type: "application/pdf" });
    setBlob(newBlob);
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;
    const reordered = Array.from(pages);
    const [removed] = reordered.splice(result.source.index, 1);
    reordered.splice(result.destination.index, 0, removed);
    setPages(reordered);
  };

  return (
    <div className="tool-box">
      <h3>üìÑ Reorder PDF Pages</h3>

      <input
        type="file"
        accept="application/pdf"
        onChange={(e) => handleFile(e.target.files[0])}
      />

      {file && (
        <>
          <Document file={file} onLoadSuccess={onDocLoad}>
            <DragDropContext onDragEnd={handleDragEnd}>
              <Droppable droppableId="pages" direction="horizontal">
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    style={{
                      display: "flex",
                      overflowX: "auto",
                      padding: "10px",
                      gap: "10px",
                    }}
                  >
                    {pages.map((pageIndex, idx) => (
                      <Draggable
                        key={`page-${pageIndex}`}
                        draggableId={`page-${pageIndex}`}
                        index={idx}
                      >
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            style={{
                              border: "1px solid gray",
                              borderRadius: "8px",
                              padding: "5px",
                              background: "#fff",
                              ...provided.draggableProps.style,
                            }}
                          >
                            <Page
                              pageNumber={pageIndex + 1}
                              width={120}
                              renderAnnotationLayer={false}
                              renderTextLayer={false}
                            />
                            <p style={{ textAlign: "center" }}>
                              Page {pageIndex + 1}
                            </p>
                          </div>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            </DragDropContext>
          </Document>

          <button onClick={handleReorder}>üîÄ Reorder & Generate</button>
        </>
      )}

      {blob && (
        <button
          onClick={() => {
            const url = URL.createObjectURL(blob);
            const link = document.createElement("a");
            link.href = url;
            link.download = "reordered.pdf";
            link.click();
          }}
        >
          ‚¨áÔ∏è Download
        </button>
      )}
    </div>
  );
}

export default ReorderPdf;
