import React, { useState } from "react";
import { PDFDocument } from "pdf-lib";
import { Document, Page, pdfjs } from "react-pdf";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

function ReorderPdf() {
  const [file, setFile] = useState(null);
  const [pages, setPages] = useState([]);
  const [numPages, setNumPages] = useState(null);
  const [blob, setBlob] = useState(null);

  const handleFile = (f) => {
    setFile(f);
    setPages([]);
    setNumPages(null);
    setBlob(null);
  };

  const onDocLoad = ({ numPages }) => {
    setNumPages(numPages);
    const initialPages = Array.from({ length: numPages }, (_, i) => ({
      index: i,
      rotation: 0,
    }));
    setPages(initialPages);
  };

  const rotatePage = (pageIdx, dir) => {
    const newPages = [...pages];
    newPages[pageIdx].rotation = (newPages[pageIdx].rotation + (dir === "left" ? -90 : 90)) % 360;
    setPages(newPages);
  };

  const deletePage = (pageIdx) => {
    const newPages = [...pages];
    newPages.splice(pageIdx, 1);
    setPages(newPages);
  };

  const handleReorder = async () => {
    const origPdf = await PDFDocument.load(await file.arrayBuffer());
    const newPdf = await PDFDocument.create();

    for (const page of pages) {
      const [copied] = await newPdf.copyPages(origPdf, [page.index]);
      copied.setRotation(degrees(page.rotation));
      newPdf.addPage(copied);
    }

    const pdfBytes = await newPdf.save();
    const newBlob = new Blob([pdfBytes], { type: "application/pdf" });
    setBlob(newBlob);
  };

  const { degrees } = PDFDocument;

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const reordered = Array.from(pages);
    const [moved] = reordered.splice(result.source.index, 1);
    reordered.splice(result.destination.index, 0, moved);
    setPages(reordered);
  };

  return (
    <div className="tool-box">
      <h3>📄 Reorder PDF Pages</h3>

      <input type="file" accept="application/pdf" onChange={(e) => handleFile(e.target.files[0])} />

      {file && numPages && (
        <Document file={file} onLoadSuccess={onDocLoad}>
          <DragDropContext onDragEnd={handleDragEnd}>
            <Droppable droppableId="pages" direction="horizontal">
              {(provided) => (
                <div className="reorder-preview" ref={provided.innerRef} {...provided.droppableProps}>
                  {pages.map((page, idx) => (
                    <Draggable key={`page-${page.index}`} draggableId={`page-${page.index}`} index={idx}>
                      {(provided) => (
                        <div
                          className="page-thumbnail"
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                        >
                          <Page
                            pageNumber={page.index + 1}
                            width={120}
                            rotate={page.rotation}
                            renderAnnotationLayer={false}
                            renderTextLayer={false}
                          />
                          <p style={{ fontSize: "12px", textAlign: "center" }}>
                            Page {page.index + 1}
                          </p>
                          <div className="button-group">
                            <button onClick={() => rotatePage(idx, "left")}>⏪</button>
                            <button onClick={() => rotatePage(idx, "right")}>⏩</button>
                            <button onClick={() => deletePage(idx)}>❌</button>
                          </div>
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </DragDropContext>
        </Document>
      )}

      {pages.length > 0 && (
        <div style={{ marginTop: "10px", display: "flex", gap: "10px" }}>
          <button onClick={handleReorder}>📄 Generate</button>
          <button onClick={() => handleFile(null)}>🔁 Reset</button>
        </div>
      )}

      {blob && (
        <button
          style={{ marginTop: "10px" }}
          onClick={() => {
            const url = URL.createObjectURL(blob);
            const link = document.createElement("a");
            link.href = url;
            link.download = "reordered.pdf";
            link.click();
          }}
        >
          ⬇️ Download
        </button>
      )}
    </div>
  );
}

export default ReorderPdf;
