import React, { useState } from "react";
import DropzoneUpload from "./DropzoneUpload";

function PdfToDocx() {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const convert = async () => {
    if (!file) return alert("üìÑ Please select a PDF to convert.");

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("file", file);
      formData.append("type", "pdf-to-docx");

      const res = await fetch("http://localhost:5001/convert", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) {
        const errText = await res.text();
        throw new Error(errText || "Conversion failed");
      }

      const blob = await res.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = url;
      link.download = file.name.replace(/\.pdf$/i, ".docx");
      link.click();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error("‚ùå Conversion Error:", err);
      alert("‚ùå Failed to convert PDF to DOCX: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      padding: "1rem",
      border: "1px solid #ccc",
      borderRadius: "8px",
      maxWidth: "450px",
      margin: "1rem auto",
      background: "#f9f9f9",
      textAlign: "center"
    }}>
      <h3>üìÑ Convert PDF ‚û°Ô∏è DOCX</h3>

      <DropzoneUpload
        onFilesSelected={(f) => setFile(f)}
        accept="application/pdf"
      />

      {file && <p>‚úÖ Selected: {file.name}</p>}

      <button
        onClick={convert}
        disabled={loading}
        style={{
          marginTop: "10px",
          padding: "8px 16px",
          backgroundColor: "#007bff",
          color: "white",
          border: "none",
          borderRadius: "4px",
          cursor: loading ? "not-allowed" : "pointer",
        }}
      >
        {loading ? "‚è≥ Converting..." : "üìÑ Convert to DOCX"}
      </button>
    </div>
  );
}

export default PdfToDocx;
