// File: src/components/ImageToPdf.jsx
import React, { useState } from "react";
import DropzoneUpload from "./DropzoneUpload";

function ImageToPdf() {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [blob, setBlob] = useState(null);

  const handleConvert = async () => {
    if (!file) return alert("‚ùó Please select an image");

    const formData = new FormData();
    formData.append("file", file);
    formData.append("type", "img-to-pdf");

    setLoading(true);
    try {
      const res = await fetch("http://localhost:5001/convert", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) throw new Error(await res.text());

      const pdfBlob = await res.blob();
      setBlob(pdfBlob);
    } catch (err) {
      console.error("‚ùå Error:", err);
      alert("Image to PDF failed: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  const download = () => {
    if (!blob) return;
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = file.name.replace(/\.\w+$/, ".pdf");
    link.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div
      style={{
        padding: "1rem",
        border: "1px solid #ccc",
        borderRadius: "8px",
        maxWidth: "400px",
        margin: "1rem auto",
        background: "#f9f9f9",
        textAlign: "center",
      }}
    >
      <h3>üñºÔ∏è Image ‚û°Ô∏è PDF</h3>

      <DropzoneUpload
        onFilesSelected={(f) => {
          setFile(f);
          setBlob(null);
        }}
        accept="image/*"
      />

      {file && <p>‚úÖ Selected: {file.name}</p>}

      <button
        onClick={handleConvert}
        disabled={loading}
        style={{
          marginTop: "10px",
          padding: "8px 16px",
          backgroundColor: "#28a745",
          color: "#fff",
          border: "none",
          borderRadius: "4px",
          cursor: loading ? "not-allowed" : "pointer",
        }}
      >
        {loading ? "‚è≥ Converting..." : "üìÑ Convert"}
      </button>

      {blob && (
        <button
          onClick={download}
          style={{
            marginLeft: "10px",
            marginTop: "10px",
            padding: "8px 16px",
            backgroundColor: "#007bff",
            color: "#fff",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
          }}
        >
          ‚¨áÔ∏è Download
        </button>
      )}
    </div>
  );
}

export default ImageToPdf;
