// File: src/components/DropzoneUpload.jsx
import React, { useCallback } from "react";
import { useDropzone } from "react-dropzone";

function DropzoneUpload({ onFilesSelected, accept }) {
  const onDrop = useCallback((acceptedFiles) => {
    if (acceptedFiles.length > 0) {
      onFilesSelected(acceptedFiles[0]);
    }
  }, [onFilesSelected]);

  // Normalize accept to an object if passed as string
  const normalizedAccept =
    typeof accept === "string" ? { [accept]: [] } : accept;

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: normalizedAccept,
    multiple: false,
  });

  return (
    <div
      {...getRootProps()}
      style={{
        border: "2px dashed #aaa",
        borderRadius: "8px",
        padding: "20px",
        textAlign: "center",
        backgroundColor: isDragActive ? "#eef" : "#fafafa",
        cursor: "pointer",
        marginBottom: "10px",
      }}
    >
      <input {...getInputProps()} />
      {isDragActive
        ? "ðŸ“‚ Drop the file here..."
        : "ðŸ“‚ Drag & Drop your file here or click to browse"}
    </div>
  );
}

export default DropzoneUpload;
