import React, { useState, useRef } from "react";
import { Document, Page } from "react-pdf";
import "./App.css"; // ‚úÖ this must exist in src/


function App() {
  const [file, setFile] = useState(null);
  const [numPages, setNumPages] = useState(null);
  const inputRef = useRef();

  const onFileChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type === "application/pdf") {
      setFile(file);
    }
  };

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
  };

  console.log("‚úÖ SafeEditPDF loaded");

  return (
    <div className="p-4 min-h-screen bg-gray-100">
      <h1 className="text-2xl font-bold mb-4 text-center">üìù SafeEditPDF</h1>
      <div className="mb-4 text-center">
        <input
          ref={inputRef}
          type="file"
          accept="application/pdf"
          onChange={onFileChange}
          className="hidden"
        />
        <button
          onClick={() => inputRef.current.click()}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Upload PDF
        </button>
      </div>

      {file && (
        <div className="flex justify-center">
          <Document
            file={file}
            onLoadSuccess={onDocumentLoadSuccess}
            onLoadError={(error) => {
              console.error("PDF load error:", error);
              alert("‚ùå Failed to load PDF.");
            }}
          >
            {Array.from(new Array(numPages), (el, index) => (
              <Page key={index + 1} pageNumber={index + 1} width={600} />
            ))}
          </Document>
        </div>
      )}
    </div>
  );
}

export default App;
