import React, { useState } from "react";
import DropzoneUpload from "./DropzoneUpload";

function MergePdf() {
  const [files, setFiles] = useState([]);
  const [merging, setMerging] = useState(false);

  const handleUpload = async () => {
    if (files.length < 2) {
      alert("‚ùó Please select at least 2 PDF files to merge.");
      return;
    }

    setMerging(true);
    const formData = new FormData();
    files.forEach((file) => formData.append("file", file)); // ‚úÖ Match backend field name
    formData.append("type", "merge-pdf");

    try {
      const res = await fetch("http://localhost:5001/convert", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) throw new Error(await res.text());

      const blob = await res.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = url;
      link.download = "merged.pdf";
      link.click();
      URL.revokeObjectURL(url);
    } catch (err) {
      alert("‚ùå Merging failed. Please check the files or try again.");
      console.error(err);
    } finally {
      setMerging(false);
    }
  };

  return (
    <div style={{ marginTop: "10px" }}>
      <h3>üîó Merge PDFs</h3>

      {/* ‚úÖ Drag & Drop Upload */}
      <DropzoneUpload
        onFilesSelected={(f) => setFiles(Array.isArray(f) ? f : [f])}
        accept="application/pdf"
        multiple={true}
      />

      {files.length > 0 && (
        <p>‚úÖ {files.length} PDF file{files.length > 1 ? "s" : ""} selected</p>
      )}

      <button onClick={handleUpload} disabled={merging}>
        {merging ? "üîÑ Merging..." : "üîó Merge PDFs"}
      </button>
    </div>
  );
}

export default MergePdf;
