import React, { useState } from "react";
import { PDFDocument } from "pdf-lib";
import { Document, Page, pdfjs } from "react-pdf";

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

function ReorderPdf() {
  const [file, setFile] = useState(null);
  const [pages, setPages] = useState([]);
  const [blob, setBlob] = useState(null);
  const [numPages, setNumPages] = useState(null);

  const handleFile = (f) => {
    setFile(f);
    setBlob(null);
    setPages([]);
    setNumPages(null);
  };

  const onDocLoad = ({ numPages }) => {
    setNumPages(numPages);
    setPages(Array.from({ length: numPages }, (_, i) => i));
  };

  const movePage = (index, direction) => {
    const newPages = [...pages];
    const targetIndex = index + direction;

    if (targetIndex >= 0 && targetIndex < newPages.length) {
      const temp = newPages[index];
      newPages[index] = newPages[targetIndex];
      newPages[targetIndex] = temp;
      setPages(newPages);
    }
  };

  const handleReorder = async () => {
    const origPdf = await PDFDocument.load(await file.arrayBuffer());
    const newPdf = await PDFDocument.create();

    for (const i of pages) {
      const [copied] = await newPdf.copyPages(origPdf, [i]);
      newPdf.addPage(copied);
    }

    const pdfBytes = await newPdf.save();
    const newBlob = new Blob([pdfBytes], { type: "application/pdf" });
    setBlob(newBlob);
  };

  return (
    <div className="tool-box">
      <h3>üìÑ Reorder PDF Pages</h3>

      <input
        type="file"
        accept="application/pdf"
        onChange={(e) => handleFile(e.target.files[0])}
      />

      {file && numPages && (
        <Document file={file} onLoadSuccess={onDocLoad}>
          <div className="reorder-preview">
            {pages.map((pageIndex, idx) => (
              <div key={pageIndex} className="page-thumbnail">
                <Page
                  pageNumber={pageIndex + 1}
                  width={120}
                  renderAnnotationLayer={false}
                  renderTextLayer={false}
                />
                <p style={{ fontSize: "12px", textAlign: "center", marginTop: "4px" }}>
                  Page {pageIndex + 1}
                </p>
                <div style={{ display: "flex", gap: "6px", justifyContent: "center" }}>
                  <button disabled={idx === 0} onClick={() => movePage(idx, -1)}>üîº</button>
                  <button disabled={idx === pages.length - 1} onClick={() => movePage(idx, 1)}>üîΩ</button>
                </div>
              </div>
            ))}
          </div>
        </Document>
      )}

      {numPages && (
        <div style={{ marginTop: "10px", display: "flex", gap: "10px" }}>
          <button onClick={handleReorder}>üìÑ Regenerate</button>
          <button onClick={() => handleFile(null)}>üîÅ Reset</button>
        </div>
      )}

      {blob && (
        <button
          style={{ marginTop: "10px" }}
          onClick={() => {
            const url = URL.createObjectURL(blob);
            const link = document.createElement("a");
            link.href = url;
            link.download = "reordered.pdf";
            link.click();
          }}
        >
          ‚¨áÔ∏è Download
        </button>
      )}
    </div>
  );
}

export default ReorderPdf;
