import React, { useState } from "react";
import DropzoneUpload from "./DropzoneUpload";

function CompressPdf() {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleCompress = async () => {
    if (!file) return alert("üìÑ Please select a PDF to compress.");

    const formData = new FormData();
    formData.append("file", file);
    formData.append("type", "compress-pdf");

    const beforeSizeKB = file.size / 1024;

    setLoading(true);
    try {
      const res = await fetch("http://localhost:5001/convert", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) throw new Error(await res.text());

      const blob = await res.blob();
      const afterSizeKB = blob.size / 1024;

      console.log(`üìÑ Original: ${beforeSizeKB.toFixed(2)} KB`);
      console.log(`üìâ Compressed: ${afterSizeKB.toFixed(2)} KB`);

      alert(
        `‚úÖ PDF Compressed!\n\nOriginal size: ${beforeSizeKB.toFixed(2)} KB\nCompressed size: ${afterSizeKB.toFixed(2)} KB\nSaved: ${(beforeSizeKB - afterSizeKB).toFixed(2)} KB`
      );

      const url = URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = url;
      link.download = file.name.replace(/\.pdf$/, "_compressed.pdf");
      link.click();
      URL.revokeObjectURL(url);
    } catch (err) {
      console.error("‚ùå Compression Error:", err);
      alert("Compression failed: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      style={{
        padding: "1rem",
        border: "1px solid rgba(237, 212, 165, 0.2)",
        borderRadius: "12px",
        maxWidth: "450px",
        margin: "1rem auto",
        background: "#191818",
        textAlign: "center",
        color: "#ffffff",
        boxShadow: "0 0 15px rgba(243, 206, 162, 0.3)",
      }}
    >
      <h3 style={{ color: "#ffd700", marginBottom: "12px" }}>üóú Compress PDF</h3>

      <DropzoneUpload
        onFilesSelected={(f) => {
          setFile(f);
        }}
        accept="application/pdf"
        multiple={false}
      />

      {file && (
        <p style={{ color: "#fefefe", fontSize: "14px" }}>
          ‚úÖ Selected: {file.name}
        </p>
      )}

      <button
        onClick={handleCompress}
        disabled={loading}
        style={{
          marginTop: "10px",
          padding: "8px 16px",
          backgroundColor: "#ff8c00",
          color: "#ffffff",
          border: "none",
          borderRadius: "6px",
          cursor: loading ? "not-allowed" : "pointer",
          fontSize: "14px",
          fontWeight: 500,
          transition: "background-color 0.2s ease",
        }}
      >
        {loading ? "üóú Compressing..." : "üóú Compress PDF"}
      </button>
    </div>
  );
}

export default CompressPdf;
