import React, { useState, useEffect } from "react";
import { Document, Page, pdfjs } from "react-pdf";
import "./App.css";

// Configure PDF.js worker
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

function App() {
  const [numPages, setNumPages] = useState(null);
  const [highlights, setHighlights] = useState([]);
  const [isDrawing, setIsDrawing] = useState(false);
  const [startPos, setStartPos] = useState(null);

  // Load highlights from localStorage
  useEffect(() => {
    const saved = localStorage.getItem("pdf-highlights");
    if (saved) {
      setHighlights(JSON.parse(saved));
    }
  }, []);

  // Save highlights to localStorage
  useEffect(() => {
    localStorage.setItem("pdf-highlights", JSON.stringify(highlights));
  }, [highlights]);

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
  };

  const handleMouseDown = (e) => {
    setIsDrawing(true);
    const rect = e.target.getBoundingClientRect();
    setStartPos({ x: e.clientX - rect.left, y: e.clientY - rect.top });
  };

  const handleMouseUp = (e, pageIndex) => {
    if (!isDrawing || !startPos) return;
    const rect = e.target.getBoundingClientRect();
    const endX = e.clientX - rect.left;
    const endY = e.clientY - rect.top;

    const text = prompt("üìù Enter note (optional):");

    const newHighlight = {
      id: Date.now(),
      page: pageIndex,
      x: Math.min(startPos.x, endX),
      y: Math.min(startPos.y, endY),
      width: Math.abs(endX - startPos.x),
      height: Math.abs(endY - startPos.y),
      text: text || "",
    };

    setHighlights((prev) => [...prev, newHighlight]);
    setIsDrawing(false);
    setStartPos(null);
  };

  const removeHighlight = (id) => {
    setHighlights((prev) => prev.filter((hl) => hl.id !== id));
  };

  const handleExport = () => {
    const blob = new Blob([JSON.stringify(highlights, null, 2)], {
      type: "application/json",
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "highlights.json";
    a.click();
    URL.revokeObjectURL(url);
  };

  const handleImport = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const data = JSON.parse(e.target.result);
        setHighlights(data);
      } catch {
        alert("Invalid JSON file.");
      }
    };
    reader.readAsText(file);
  };

  return (
    <div className="p-4 min-h-screen bg-gray-100">
      <h1 className="text-2xl font-bold mb-4 text-center">üìù SafeEditPDF</h1>

      <div className="flex flex-wrap justify-center gap-3 mb-4">
        <button onClick={handleExport} className="bg-green-600 text-white px-3 py-1 rounded">
          üì§ Export Highlights
        </button>

        <label className="bg-yellow-500 text-white px-3 py-1 rounded cursor-pointer">
          üì• Import Highlights
          <input type="file" accept="application/json" onChange={handleImport} className="hidden" />
        </label>

        <button onClick={() => window.print()} className="bg-blue-700 text-white px-3 py-1 rounded">
          üñ®Ô∏è Print PDF
        </button>

        <a
          href="/test.pdf"
          download="SafeEditPDF.pdf"
          className="bg-gray-700 text-white px-3 py-1 rounded"
        >
          ‚¨áÔ∏è Download PDF
        </a>
      </div>

      <div className="flex justify-center">
        <Document
          file="/test.pdf"
          onLoadSuccess={onDocumentLoadSuccess}
          onLoadError={(error) => {
            console.error("PDF load error:", error);
            alert("‚ùå Failed to load test.pdf.");
          }}
        >
          {Array.from(new Array(numPages), (_, index) => (
            <div key={index} style={{ position: "relative", marginBottom: "1rem" }}>
              <Page pageNumber={index + 1} width={600} />

              <div
                style={{
                  position: "absolute",
                  top: 0,
                  left: 0,
                  width: 600,
                  height: "100%",
                  cursor: "crosshair",
                }}
                onMouseDown={handleMouseDown}
                onMouseUp={(e) => handleMouseUp(e, index)}
              >
                {highlights
                  .filter((hl) => hl.page === index)
                  .map((hl) => (
                    <div
                      key={hl.id}
                      title="Click to edit or remove"
                      onClick={(e) => {
                        e.stopPropagation();
                        const text = prompt("Edit note or leave blank to delete", hl.text);
                        if (text === null) return;
                        if (text === "") removeHighlight(hl.id);
                        else {
                          setHighlights((prev) =>
                            prev.map((h) =>
                              h.id === hl.id ? { ...h, text } : h
                            )
                          );
                        }
                      }}
                      style={{
                        position: "absolute",
                        top: hl.y,
                        left: hl.x,
                        width: hl.width,
                        height: hl.height,
                        backgroundColor: "rgba(255, 255, 0, 0.4)",
                        border: "1px solid orange",
                        pointerEvents: "auto",
                        cursor: "pointer",
                      }}
                    >
                      {hl.text && (
                        <div
                          style={{
                            fontSize: "10px",
                            backgroundColor: "black",
                            color: "white",
                            padding: "2px 4px",
                            position: "absolute",
                            top: -18,
                            left: 0,
                            borderRadius: "4px",
                            whiteSpace: "nowrap",
                          }}
                        >
                          {hl.text}
                        </div>
                      )}
                    </div>
                  ))}
              </div>
            </div>
          ))}
        </Document>
      </div>
    </div>
  );
}

export default App;
