import React, { useState, useRef } from "react";
import { Document, Page, pdfjs } from "react-pdf";
import { PDFDocument, rgb, StandardFonts } from "pdf-lib";
import jsPDF from "jspdf";
import mammoth from "mammoth";
import PdfToDocx from "./components/PdfToDocx";
import DocxToPdf from "./components/DocxToPdf";
import ImageToPdf from "./components/ImageToPdf";
import MergePdf from "./components/MergePdf";
import SplitPdf from "./components/SplitPdf";
import "./App.css";
import "react-pdf/dist/Page/AnnotationLayer.css";
import "react-pdf/dist/Page/TextLayer.css";
import OcrPdf from "./components/OcrPdf";

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

function App() {
  const [pdfFile, setPdfFile] = useState(null);
  const [numPages, setNumPages] = useState(null);
  const [textElements, setTextElements] = useState([]);
  const [selectedTextId, setSelectedTextId] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [fontSize, setFontSize] = useState(12);
  const [fontColor, setFontColor] = useState("#000000");
  const [fontFamily, setFontFamily] = useState("Arial");
  const fileInputRef = useRef();

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type === "application/pdf") {
      setPdfFile(file);
      setTextElements([]);
    }
  };

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
  };

  const handlePageClick = (e, pageIndex) => {
    if (selectedTextId) return;
    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const input = prompt("Enter text to insert:");
    if (input) {
      setTextElements((prev) => [
        ...prev,
        {
          id: Date.now(),
          page: pageIndex,
          x,
          y,
          text: input,
          size: fontSize,
          color: fontColor,
          font: fontFamily,
          bold: false,
          italic: false,
          underline: false,
        },
      ]);
    }
  };

  const handleDownload = async () => {
    if (!pdfFile) return;
    const arrayBuffer = await pdfFile.arrayBuffer();
    const pdfDoc = await PDFDocument.load(arrayBuffer);
    const helveticaFont = await pdfDoc.embedFont(StandardFonts.Helvetica);

    textElements.forEach((el) => {
      const page = pdfDoc.getPage(el.page);
      const { height } = page.getSize();
      page.drawText(el.text, {
        x: el.x,
        y: height - el.y - el.size,
        size: el.size,
        font: helveticaFont,
        color: rgb(
          parseInt(el.color.slice(1, 3), 16) / 255,
          parseInt(el.color.slice(3, 5), 16) / 255,
          parseInt(el.color.slice(5, 7), 16) / 255
        ),
      });
    });

    const modifiedPdf = await pdfDoc.save();
    const blob = new Blob([modifiedPdf], { type: "application/pdf" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "EditedPDF.pdf";
    link.click();
  };

  const startDrag = (e, id) => {
    e.stopPropagation();
    const el = textElements.find((t) => t.id === id);
    setSelectedTextId(id);
    setDragOffset({ x: e.clientX - el.x, y: e.clientY - el.y });
  };

  const onDrag = (e) => {
    if (!selectedTextId) return;
    setTextElements((prev) =>
      prev.map((t) =>
        t.id === selectedTextId
          ? { ...t, x: e.clientX - dragOffset.x, y: e.clientY - dragOffset.y }
          : t
      )
    );
  };

  const stopDrag = () => setSelectedTextId(null);

  const updateText = (id, newText) => {
    setTextElements((prev) =>
      prev.map((el) => (el.id === id ? { ...el, text: newText } : el))
    );
  };

  const exportAnnotations = () => {
    const blob = new Blob([JSON.stringify(textElements, null, 2)], {
      type: "application/json",
    });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "annotations.json";
    link.click();
  };

  const importAnnotations = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (ev) => {
        const annotations = JSON.parse(ev.target.result);
        setTextElements(annotations);
      };
      reader.readAsText(file);
    }
  };

  const handleDocxToPDF = () => {
    const input = document.createElement("input");
    input.type = "file";
    input.accept = ".docx";
    input.onchange = async (e) => {
      const file = e.target.files[0];
      const reader = new FileReader();
      reader.onload = async (event) => {
        const arrayBuffer = event.target.result;
        const result = await mammoth.convertToHtml({ arrayBuffer });
        const doc = new jsPDF();
        doc.html(result.value, {
          callback: (doc) => doc.save("converted.pdf"),
          x: 10,
          y: 10,
        });
      };
      reader.readAsArrayBuffer(file);
    };
    input.click();
  };

  const handleImageToPDF = () => {
    const input = document.createElement("input");
    input.type = "file";
    input.accept = "image/*";
    input.onchange = (e) => {
      const file = e.target.files[0];
      const reader = new FileReader();
      reader.onload = (event) => {
        const img = new Image();
        img.src = event.target.result;
        img.onload = () => {
          const pdf = new jsPDF();
          pdf.addImage(img, "JPEG", 10, 10, 190, 270);
          pdf.save("image.pdf");
        };
      };
      reader.readAsDataURL(file);
    };
    input.click();
  };

  return (
    <div
      className="container"
      onMouseMove={onDrag}
      onMouseUp={stopDrag}
      onClick={() => setSelectedTextId(null)}
    >
      <h1>üìù SafeEditPDF</h1>

  
      {/* Tools Section - Well Styled */}
      <div className="tools-group">
        <div className="tool-box">
          <h3>üìÑ Convert PDF ‚û°Ô∏è DOCX</h3>
          <PdfToDocx />
        </div>
        <div className="tool-box">
          <h3>üìÑ DOCX ‚û°Ô∏è PDF</h3>
          <DocxToPdf />
        </div>
        <div className="tool-box">
          <h3>üñºÔ∏è Image ‚û°Ô∏è PDF</h3>
          <ImageToPdf />
        </div>
        <div className="tool-box">
          <h3>üîó Merge PDFs</h3>
          <MergePdf />
        </div>
        <div className="tool-box">
          <h3>‚úÇÔ∏è Split PDF</h3>
          <SplitPdf />
        </div>
        <div className="tool-box">
          <h3>üóú Compress PDF</h3>
          <CompressPdf />
        </div>
        <div className="tool-box">
          <h3>üîç OCR PDF</h3>
          <OcrPdf />
        </div>
      </div>

      {pdfFile && (
        <Document file={pdfFile} onLoadSuccess={onDocumentLoadSuccess} className="pdf-container">
          {Array.from(new Array(numPages), (_, index) => (
            <div key={index} style={{ position: "relative", marginBottom: "1rem" }}>
              <Page pageNumber={index + 1} width={600} onClick={(e) => handlePageClick(e, index)} />
              <div className="overlay" style={{ position: "absolute", top: 0, left: 0, width: 600, height: "100%" }}>
                {textElements
                  .filter((t) => t.page === index)
                  .map((t) => (
                    <div
                      key={t.id}
                      style={{
                        position: "absolute",
                        left: t.x,
                        top: t.y,
                        fontSize: t.size,
                        fontFamily: t.font,
                        color: t.color,
                        fontWeight: t.bold ? "bold" : "normal",
                        fontStyle: t.italic ? "italic" : "normal",
                        textDecoration: t.underline ? "underline" : "none",
                        cursor: "move",
                        background: "#fff",
                        padding: "2px",
                        border: "1px dashed #ccc",
                      }}
                      onMouseDown={(e) => startDrag(e, t.id)}
                      onDoubleClick={() => {
                        const newText = prompt("Edit text:", t.text);
                        if (newText) updateText(t.id, newText);
                      }}
                    >
                      {t.text}
                    </div>
                  ))}
              </div>
            </div>
          ))}
        </Document>
      )}
    </div>
  );
}

export default App;
